                        "anyValue": true,
                        "possibleValues": null
                    }
                }
            },
            "minOccurs": 1,
            "maxOccurs": 1
        },
        {
            "id": "epoch",
            "title": "epoch",
            "description": "",
            "input": {
                "literalDataDomain": {
                    "dataType": "value",
                    "valueDefinition": {
                        "anyValue": true,
                        "possibleValues": null
                    }
                }
            },
            "minOccurs": 1,
            "maxOccurs": 1
        },
        {
            "id": "points",
            "title": "points",
            "description": "",
            "input": {
                "literalDataDomain": {
                    "dataType": "value",
                    "valueDefinition": {
                        "anyValue": true,
                        "possibleValues": null
                    }
                }
            },
            "minOccurs": 1,
            "maxOccurs": 999999
        }
    ],
    "outputs": [
        {
            "id": "dfc",
            "title": "dfc",
            "description": "",
            "output": {
                "transmissionMode": [
                    "value"
                ]
            }
        }
    ]
}

